targets.expr:
topk(
  10, sum by (http_route, http_request_method, method_route) (
    label_join(
      max_over_time(
        http_server_request_duration_seconds_count{job="$job", instance="$instance", http_route!=""}[$__rate_interval]), "method_route", " ", "http_request_method", "http_route"
      )
    )
  )

topk(
    15, histogram_quantile(
        .95, sum(
            rate(
                traces_spanmetrics_latency_bucket{service_name="$job", service_instance_id="$instance", span_name="database"}[$__rate_interval]
            )
        ) by (
            le, service_name, service_instance_id, db_name, db_statement
        )
    ) > 0
)

topk(
    15, histogram_quantile(
        .95, sum(
            rate(
                traces_spanmetrics_latency_bucket{service_name="$job", service_instance_id="$instance", span_name="database"}[$__rate_interval]
            )
        ) by (
            le, service_name, service_instance_id, db_name, db_statement
        )
    ) > 3
)
